    src: url('/modules/inc/preloader_new/fonts/Gotham-Book.ttf') format('truetype'), url('/modules/inc/preloader_new/fonts/Gotham-Book.eot') format('embedded-opentype'), url('/modules/inc/preloader_new/fonts/Gotham-Book.woff') format('woff');
  }
  @font-face { font-family:'GothamBold'; src: url('/modules/inc/preloader_new/fonts/GothaProBold.otf') format('opentype')}
  @font-face {
    font-family: 'Gotham'; src: url('/modules/inc/preloader_new/fonts/Gotham-Ultra.eot'); src: url('/modules/inc/preloader_new/fonts/Gotham-Ultra.eot?#iefix') format('embedded-opentype'),
        url('/modules/inc/preloader_new/fonts/Gotham-Ultra.woff2') format('woff2'), url('/modules/inc/preloader_new/fonts/Gotham-Ultra.woff') format('woff');
    font-weight: 500; font-style: normal;
}

body {
    margin: 0;
    padding: 0;
}

.preloader-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    box-sizing: border-box;
    overflow-x: hidden;
    overflow-y: auto;
    z-index: 10000;
    min-height: 100vh;
    background-size: cover;
    background-position: center top;
    background-repeat: no-repeat;
    font-family: sans-serif;
}

.preloader-svg {
    width: 100%;
    height: 100%;
}

.preloader__saga-container {
  display: flex;
  align-items: flex-start;
  display: -webkit-flex;
  -webkit-align-items: flex-start;
  min-width: 320px;
  margin-bottom: 0px;
}

.preloader__saga-logo {
    width: 170px;
    margin-left: -40px;
    margin-bottom: 20px;
}

.loading {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    stroke: #fff;
    width: 75px;
    height: 75px;
}

.precent-block {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
.preloader__chicago-logo{
  max-width: 360px;
  margin-bottom: 40px;
}

/* Circle start */
@keyframes svg-circle {
    from { opacity: 0; transform: translate(-10%, 10%); }
    to { opacity: 1; transform: translate(0, 0); }
}

.svg-circle {
    stroke: #ffffff;
    fill: #fff;
    stroke-linecap: round;
    animation: svg-circle 2s;
    animation-duration: 1s;
    animation-delay: 0.5s;
    animation-fill-mode: both;
}
/* Circle end */

/* Letters start */
.svg-letters {
    fill: #405174;
}
@keyframes svg-letters_upper {
    from { opacity: 0; }
    to { opacity: 1; }
}

.svg-letters_upper path{
    opacity: 0;
    animation-name: svg-letters_upper;
    animation-fill-mode: forwards;
    animation-duration: 1s;
    z-index: 2;
    fill: #405174;
    stroke: #405174;
}

.svg-Saga {animation-delay: 1.3s;}
.svg-sAga {animation-delay: 1.5s;}
.svg-saGa {animation-delay: 1.7s;}
.svg-sagA {animation-delay: 1.9s;}

.svg-letters_lower {
    opacity: 0;
    animation-name: svg-letters_upper;
    animation-fill-mode: forwards;
    animation-duration: 1s;
    animation-delay: 2.5s;
    fill: #405174;
    stroke: #405174;
}
/* Letters end */

/* Loading circle start */
@keyframes svg-loading-cirlce {
    from {stroke-dashoffset: 236;}
    to {stroke-dashoffset: 0;}
}

.svg-loading-circle {
    stroke:#b5b5b5;
    stroke-width: 2px;
    fill: none;
    stroke: #fff;
    stroke-dasharray: 236;
    stroke-dashoffset: 236;
    animation-name: svg-loading-cirlce;
    animation-fill-mode: forwards;
    animation-duration: 5s;
}

.svg-loading-circle__text {
    font-family: sans-serif;
    font-size: 16px;
    fill: #b5b5b5;
}
/* Loading circle end */

/* Outer lines start */
@keyframes svg-outer-lines {
    0% {stroke-dashoffset: 2684;}
    50% {opacity:1;}
    100% {stroke-dashoffset: 0; opacity: 0;}
}
.svg-outer-lines path {
    stroke: #ffffff;
    stroke-width: 2;
    stroke-dasharray: 2684;
    stroke-dashoffset: 2684;
    animation-name: svg-outer-lines;
    animation-duration: 0.7s;
    animation-delay: 1.5s;
    animation-fill-mode: forwards;
    position: relative;
}
path.svg-outer_s {animation-delay: 1.6s;}
path.svg-outer_a_1_3 {animation-delay: 1.7s;}
path.svg-outer_a_2 {animation-delay: 1.8s;}
path.svg-outer_a_1_1 {animation-delay: 1.9s;}
path.svg-outer_g {animation-delay: 2s;}
path.svg-outer_a_1_2 {animation-delay: 2.1s;}
/* Outer lines end */

/* Inner lines start */
@keyframes svg-inner-lines {
    from {stroke-dashoffset: 185;}
    to {stroke-dashoffset: 0;}
}

.svg-inner-lines path {
    /*stroke:#405174;*/
    stroke:#405174;
    stroke-width: 2;
    stroke-dasharray: 185;
    stroke-dashoffset: 185;
    animation-name: svg-inner-lines;
    animation-duration: 1s;
    animation-delay: 1.7s;
    animation-fill-mode: forwards;
}

path.svg-inner_s {animation-delay: 1.8s;}
path.svg-inner_a_1_3 {animation-delay: 1.9s;}
path.svg-inner_a_2 {animation-delay: 2s;}
path.svg-inner_a_1_1 {animation-delay: 2.1s;}
path.svg-inner_g {animation-delay: 2.2s;}
path.svg-inner_a_1_2 {animation-delay: 2.3s;}
/* Inner lines end */

/* Svg remove content start */

@keyframes removeCircle {
    15% {transform: translateY(20px);opacity:1}
    100%{transform: translateY(-400px);opacity:0;}
}

.remove-svg .svg-circle {
    animation-name: removeCircle;
    animation-duration: 1s;
    animation-delay: 0.25s;
    animation-fill-mode: forwards;
}
.remove-svg .svg-letters_upper {
    animation-name: removeCircle;
    animation-duration: 1s;
    animation-delay: 0.25s;
    animation-fill-mode: forwards;
}
.remove-svg .svg-inner-lines {
    animation-name: removeCircle;
    animation-duration: 1s;
    animation-delay: 0.25s;
    animation-fill-mode: forwards;
}
.remove-svg .svg-loading-circle-container {
    animation-name: removeCircle;
    animation-duration: 1s;
    animation-delay: .2s;
    animation-fill-mode: forwards;
}
.remove-svg.preloader-container {
    opacity: 0;
    transform: translateY(-150%);
}



/* message start */
.preloader__wrapper {
  height: 100vh;
  display: flex;
  justify-content: space-between;
  align-items: center;
  display: -webkit-flex;
  -webkit-justify-content: space-between;
  -webkit-align-items: center;
  background-color:  #000000;
  line-height: 1.1;
}
.preloader__right {
  padding-left: 46px;
  width: 65%;
  color: #ffffff;
