  height: auto;
}
.checked_message{
  /* font-family: 'Gotham Book'; */
  font-family: 'Montserrat', sans-serif;
  font-weight: 700;
  text-transform: uppercase;
  font-size: 40px;
  color: #ffffff;
  opacity: 0;
  animation-name: preloaderMessage;
  animation-duration: 2s;
  animation-fill-mode: forwards;
  animation-delay: .8s;
  transform: translateX(60px);
}

.preloader__word_2 {
    animation-delay: .5s;
}

@keyframes preloaderMessage {
    0% {opacity: 0; transform: translateX(60px)}
    100% {opacity: 1; transform: translateX(0px)}
}
/* message end */
.preloader__newyork-logo {
    max-width: 280px;
    width: 100%;
    fill: #ffffff;
    margin-bottom: 20px;
    opacity: 0;
    transform: translate(40px, 40px);
    animation-name: chicagoLogo;
    animation-duration: 2s;
    animation-fill-mode: forwards;
    -webkit-filter: drop-shadow( 5px 19px 7px rgba(40, 39, 39, 0.7));
            filter: drop-shadow( 5px 19px 7px rgba(40, 39, 39, 0.7));
}
@keyframes chicagoLogo {
    0% {opacity:0; transform: translate(40px, 40px);}
    100% {opacity: 1; transform: none;}
}
/* preloader bottom text start*/
.preloader__bottom-text {
    font-family: 'Gotham Book', Arial, Helvetica, sans-serif;
    font-size: 12px;
    color: #ffffff;
    height: 50px;
    padding-left: 190px;
}

@media screen and (max-width: 1900px) {
    .preloader__word {
        font-size: 50px;
    }
}

@media screen and (max-width: 1200px) {
    .preloader__word {
        font-size: 40px;
    }
    .checked_message{
      font-size: 36px;
    }
    .checked_svg {
    width: 40px;
}

}

@media screen and (max-width: 768px) {
